import React, { Component } from 'react'
import logo from '../../../assets/hatch_01_DR_Prop_DinoEgg_0001.png'
import NtpTimeSync from "ntp-time-sync"

class Header extends Component {

  componentDidMount(){

  }
  async calcTimeRemaining(){
    // ? get the global time from an api, compare that to the release date, display the countdown and change the image to the one representing how close to the date it is.
    // let timeInst = NtpTimeSync.getInstance();
    // const result = await timeInst.getTime();
    // console.log("real time", result.now);


    var ntpClient = require('ntp-client');

    ntpClient.getNetworkTime("pool.ntp.org", 123, function (err, date) {
      if (err) {
        console.error(err);
        return;
      }

      console.log("Current time : ");
      console.log(date); // Mon Jul 08 2013 21:31:31 GMT+0200 (Paris, Madrid (heure d’été))
    });
  }
  render(){

    return(
      <header className="dino-header">

        <img src={logo} className="App-logo" alt="logo" />
      </header>
    )
  }
}
export default Header
