import React, { Component } from 'react'
import logo from '../../../assets/hatch_01_DR_Prop_DinoEgg_0001.png'
import NtpTimeSync from "ntp-time-sync"

class Header extends Component {

  calcTimeRemaining(){
    // ? get the global time from an api, compare that to the release date, display the countdown and change the image to the one representing how close to the date it is.
    let timeInst = NtpTimeSync.getInstance();
    timeInst.get.then(function (result) {
      console.log("current system time", new Date());
      console.log("real time", result.now);
      console.log("offset in milliseconds", result.offset);
    })

  }
  render(){
    this.calcTimeRemaining();
    return(
      <header className="dino-header">

        <img src={logo} className="App-logo" alt="logo" />
      </header>
    )
  }
}
export default Header
